import React from 'react'
import { Form, TextArea, Button, Input } from 'semantic-ui-react'
import { useState } from 'react'
import { useDispatch } from "react-redux"
import { addNote } from '../redux/notes/notesSlice'
function FormNotes() {
  const [title, setTitle] = useState("")
  const [textarea, setTextarea] = useState("")
  const [color, setColor] = useState("blue")
  const [count, setCount] = useState(4)
  const handleButtonClick = () => {
    setColor()
  }
  const dispatch = useDispatch()
  const handleButtonSubmit = (e) => {
    e.preventDefault()
    dispatch(addNote({
      id: count,
      titlearea: title,
      textarea: textarea,
      color: "blue"
    }))
    setTitle("")
    setTextarea("")
    setCount(count + 1)
  }
  return (
    <div>
      <Form>
        <Input icon='text' placeholder='Enter your title here...' value={title} onChange={(e) => setTitle(e.target.value)} />
        <TextArea placeholder='Enter your note here...' value={textarea} onChange={(e) => setTextarea(e.target.value)} />
        <Button color='pink' onClick={handleButtonClick} />
        <Button color='purple' onClick={handleButtonClick} />
        <Button color='yellow' onClick={handleButtonClick} />
        <Button color='blue' onClick={handleButtonClick} />
        <Button color='green' onClick={handleButtonClick} />
        <Button color='green' onClick={handleButtonSubmit}>ADD</Button>
      </Form>
    </div>
  )
}

export default FormNotes
