import React from 'react'
import { Form, TextArea, Button, Input } from 'semantic-ui-react'
import { useState } from 'react'
import { useDispatch } from "react-redux"
import { addNote } from '../redux/notes/notesSlice'

function FormNotes() {
  const [title, setTitle] = useState("")
  const [textarea, setTextarea] = useState("")
  const [color, setColor] = useState("blue")
  const [count, setCount] = useState(4)
  const handleButtonClick = (e) => {
    setColor(e.target.id)
  }
  const dispatch = useDispatch()
  const handleButtonSubmit = (e) => {
    e.preventDefault()
    dispatch(addNote({
      id: count,
      titlearea: title,
      textarea: textarea,
      color: color
    }))
    setTitle("")
    setTextarea("")
    setCount(count + 1)
  }
  return (
    <div>
      <Form>
        <Input icon='text' placeholder='Enter your title here...' value={title} onChange={(e) => setTitle(e.target.value)}
        style={{marginRight:"114vh"}} />
        <TextArea style={{minHeight:"150px"}} placeholder='Enter your note here...' value={textarea} onChange={(e) => setTextarea(e.target.value)} />
        <Button style={{ height: "50px", width: "50px", borderRadius: "50px", padding:"20px", marginTop:"20px",marginBottom:"20px" }} id="pink" color='pink' onClick={handleButtonClick} >{color === "pink" ? "ðŸ—¸" : ""}</Button>
        <Button style={{ height: "50px", width: "50px", borderRadius: "50px", padding:"20px", marginTop:"20px",marginBottom:"20px" }} id="purple" color='purple' onClick={handleButtonClick} >{color === "purple" ? "ðŸ—¸" : ""}</Button>
        <Button style={{ height: "50px", width: "50px", borderRadius: "50px", padding:"20px", marginTop:"20px",marginBottom:"20px" }} id="yellow" color='yellow' onClick={handleButtonClick} >{color === "yellow" ? "ðŸ—¸" : ""}</Button>
        <Button style={{ height: "50px", width: "50px", borderRadius: "50px", padding:"20px", marginTop:"20px",marginBottom:"20px" }} id="blue" color='blue' onClick={handleButtonClick} >{color === "blue" ? "ðŸ—¸" : ""}</Button>
        <Button style={{ height: "50px", width: "50px", borderRadius: "50px", padding:"20px", marginTop:"20px",marginBottom:"20px" }} id="green" color='green' onClick={handleButtonClick}>{color === "green" ? "ðŸ—¸" : ""}</Button>
        <Button id="green" color='green' onClick={handleButtonSubmit} style={{marginLeft:"39vh",position:"absolute"}}>ADD</Button>
      </Form>
    </div>
  )
}

export default FormNotes
