import React from 'react'
import { Form, TextArea, Button } from 'semantic-ui-react'
import { useState } from 'react'
import {useDispatch} from "react-redux"
import { addNote } from '../redux/notes/notesSlice'
  function FormNotes() {
    const [title,setTitle] = useState("")
    const [color, setColor] = useState("blue")
    const handleButtonClick=()=>{
      setColor()
    }
    const dispatch = useDispatch()
    const handleButtonSubmit=(e)=>{
      e.preventDefault()
      dispatch(addNote({id:"3",title}))
      setTitle("")
    }
    return (
      <div>
        <Form>
          <TextArea placeholder='Enter your note here...' value={title} onChange={(e)=> setTitle(e.target.value)} />
          <Button color='pink' onClick={handleButtonClick}/>
          <Button color='purple' onClick={handleButtonClick}/>
          <Button color='yellow' onClick={handleButtonClick}/>
          <Button color='blue' onClick={handleButtonClick}/>
          <Button color='green' onClick={handleButtonClick}/>
          <Button color='green' onClick={handleButtonSubmit}>ADD</Button>
        </Form>
      </div>
    )
  }

export default FormNotes
