import { createSlice } from "@reduxjs/toolkit";

export const todosSlice = createSlice({
    name: "notes",
    initialState:{
        items: [
            {
                id: 1,
                titlearea: "texttitle",
                textarea: "textnote",
                color: "textcolor"
            }
        ]
    },
    reducers:{}
})

export default notesSlice.reducer
