import React from 'react'
import { Form, TextArea, Button } from 'semantic-ui-react'
import { useState } from 'react'
  function FormNotes() {
    const [color, setColor] = useState("blue")
    const handleButtonClick=()=>{
      setColor()
    }
    return (
      <div>
        <Form>
          <TextArea placeholder='Tell us more' />
          <Button color='pink' onClick={handleButtonClick}/>
          <Button color='purple' onClick={handleButtonClick}/>
          <Button color='yellow' onClick={handleButtonClick}/>
          <Button color='blue' onClick={handleButtonClick}/>
          <Button color='green' onClick={handleButtonClick}/>
        </Form>
      </div>
    )
  }

export default FormNotes
