import { createSlice } from "@reduxjs/toolkit";

export const todosSlice = createSlice({
    name: "notes",
    initialState:{
        items: [
            {
                titlearea: texttitle,
                textarea: textnote,
                color: textcolor
            }
        ]
    },
    reducers:{}
})

export default todosSlice.reducer
